import React,{Component} from 'react';
import {
        ListItem,
        ListInfo,
        ListLine,
        ListAciton,
} from '../style';
import {connect} from 'react-redux';


class List extends Component{
    render(){
        const {List}=this.props;
        return (
            <div>    
                {/* <ListLine/> */}
                {
                    List.map((item)=>{
                       return(
                        <ListItem key={item.get('id')}>
                            <img className="pic" alt= "" src="//upload-images.jianshu.io/upload_images/14524620-b3352a48f9e7cf13.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/360/h/240"></img>
                            <ListInfo>
                                <h3 className="title">年轻、任性、脾气大的女人，但愿你能听懂舒淇讲的这段话</h3>
                                <p className="desc">文|李弯湾 放眼全世界，中国的年轻一代的女人，不说社会地位吧，光说家庭地位，应该是全球最高的之一。如果这个描述不够准确，那么把范围在缩小一点—...</p>
                                <ListAciton>
                                <a className="action" href="/">极物</a>
                                <a className="action" href="/detail"><span className="iconfont">&#xe684;</span>36</a>
                                <span className="action"><span className="iconfont">&#xe65c;</span>734</span>
                                </ListAciton>
                            </ListInfo>
                        </ListItem>
                       ) 
                    })
                }

            </div>

        )
    }
}

const mapState=(state)=>({
    list:state.getIn(['home','articleList'])
})
export default List;
