import {fromJS} from 'immutable'
import { constants } from '.';

const defaultState =fromJS({
    articleList:[],
    recommendsList:[],
    writersList:[]
})

export default (state=defaultState,action)=>{
    switch(action.type){
        case constants.CHANGE_HOME_INFO:
         return  state.merge({
            articleList:fromJS(action.articleList),
            recommendsList:fromJS(action.recommendsList),
            writersList:fromJS(action.writersList)
         }
        )

        case constants.CHANGE_MORE_INFO:
        // // return console.log(action.list);

        return state.set('articleList',state.get('articleList').concat(Faction.list));
       default:
       return state;
    }

}
