export const getDetailInfo=(id)=>({
 type:constants.GET_DETAIL_INFO,
 id
})

export const changeDetailInfo=(newTitle,newContent)=>({
    type:constants.CHANGE_DETAIL_INFO,
    title:newTitle,
    content:newContent

})

