import React,{Component} from 'react';
import {
        ListItem,
        ListInfo,
        ListLine,
        ListAciton,
        LoadMore
} from '../style';
import {connect} from 'react-redux';
import { actionCreators } from '../store';


class List extends Component{
    render(){
        const {list,handleLoadMore,page}=this.props;
        return (
            <div>    
                <ListLine/>
                {
                    list.map((item,index)=>{
                       return(
                        <ListItem key={index}>
                            <img 
                                className="pic" 
                                alt= "" 
                                src={item.get('imgUrl')}
                            />
                            <ListInfo>
                                <a className="title">{item.get('title')}</a>
                                <p className="desc">{item.get('desc')}</p>
                                <ListAciton>
                                <a className="action" href="/">{item.get('writer')}</a>
                                <a className="action" href="/detail"><span className="iconfont">&#xe684;</span>{item.get('commend')}</a>
                                <span className="action"><span className="iconfont">&#xe65c;</span>{item.get('praise')}</span>
                                </ListAciton>
                            </ListInfo>
                        </ListItem>
                       ) 
                    })
                }
                <LoadMore onClick={()=>handleLoadMore(page)}>阅读更多</LoadMore>
            </div>

        )
    }
}

const mapStateToProps=(state)=>({
    list:state.getIn(['home','articleList']),
    page:state.getIn(['home','articlePage'])
})

const mapDispatchToProps=(dispatch)=>({
        handleLoadMore(page){
            const action=actionCreators.loadMoreInfo(page);
            dispatch(action);
        }
})

export default connect(mapStateToProps,mapDispatchToProps)(List);
