import {fromJS} from 'immutable'
import { merge } from 'rxjs';


const defaultState =fromJS({
 articleList:[],
recommendsList:[],
writersList:[]
})

export default (state=defaultState,action)=>{
    switch(action.type){
        case 'change_home_data':
         state.merge({
            articleList:fr
         }
        )
       default:
       return state;
    }

}
