import React,{Component} from 'react';
import {
         WritersWrapper,
         WritersInfoTitle,
         WritersInfoSwitch, 
         WritersInfoList, 
         WritersInfoItem 
        } from '../style';
import {connect} from 'react-redux';

class Writers extends Component{
    render(){
        const {list}=this.props;
        return (
           <WritersWrapper>
               <WritersInfoTitle>
                   推荐作者
                    <WritersInfoSwitch>
                        <span className  ="iconfont">&#xe851;</span>
                        换一批
                    </WritersInfoSwitch>
               </WritersInfoTitle>
                    <WritersInfoList>
               {
                  list.map((item)=>{
                      return (
                        <WritersInfoItem>
                        <img className="user" alt="" src={item.get('imgUrl')}/>
                        <a href="/detail" className="follow"> 
                             <span className="iconfont">&#xe613;</span>
                             关注
                        </a>
                        <a href="/detail" className={item.get('name')}>
                        梅花开
                        </a>
                        <p className={"writerinfo"}>写了261.7k字 · 22.7k喜欢</p>
                    </WritersInfoItem>
                      )

                  })
               }
                    </WritersInfoList> 


           </WritersWrapper>
        )
    }
}

const mapStateToProps=(state)=>({
    list:state.getIn('home','writerList')
})

export default connect(mapStateToProps)(Writers);
