import React,{Component} from 'react';
import {HomeWrapper,
        HomeLeft,
        HomeRight, 
        BackTop,   
    } from './style';
import {connect} from 'react-redux';
import List from './components/List';
import Recommends from './components/Recommends';
import Writers from './components/Writers';
import Download from './components/Download';
import {actionCreators} from './store';

class Home extends Component{
    render(){
        const {handleScrollTop}=this.props;
        return (
            <HomeWrapper>
                <HomeLeft>
                    <img className="banner-img" alt="" src="//upload.jianshu.io/admin_banners/web_images/4606/5d7f5855af24cb8cd161f37d9f9af47342714f34.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/1250/h/540"></img>
                    <List/>
                </HomeLeft>
                <HomeRight>
                    <Recommends/>
                    <Download/>
                    <Writers/>
                </HomeRight>
                <BackTop onClick={handleScrollTop}>顶部</BackTop>
            </HomeWrapper>
        )
    }

    componentDidMount(){
        this.props.changeHomeData();

    }

}


const mapDispatchToProps=(dispatch)=>({
    changeHomeData(){
        const action=actionCreators.getHomeInfo();
        dispatch(action);
    },
    handleScrollTop(){
        document.documentElement.scrollTop = 0
    }
    })


export default connect(null,mapDispatchToProps)(Home);
