import {fromJS} from 'immutable'
import { constants } from './';


const defaultState =fromJS({
  title:["111"],
  onClose:false,
  // content:'<img data-original-src="//upload-images.jianshu.io/upload_images/2535252-abc3047b950c6428" data-original-width="640" data-original-height="426" data-original-format="image/jpeg" data-original-filesize="49886" class="" style="cursor: zoom-in;" src="//upload-images.jianshu.io/upload_images/2535252-abc3047b950c6428?imageMogr2/auto-orient/strip%7CimageView2/2/w/640/format/webp">'
})

export default (state=defaultState,action)=>{
    switch(action.type){
       case constants.CHANGE_DETAIL_INFO:
          return state.merge({
            title:fromJS(action.title)
          })
        case constants.CHANGE_CLOSE_ON:
        return state.merge({
          onClose:fromJS(action.onClose)
        })

       default:
       return state;
    }

}
