import styled from 'styled-components';
import wujiaoPic from '../../statics/wujiao.png';

export const InfoWrapper=styled.div`
margin:0 auto;
padding-top:20px;
padding-bottom:40px;
width:620px;

`
export const InfoTitle=styled.div`
font-size:34px;
line-height:1.3;
font-weight:700;
padding-top:20px;

`
export const WriterWrapper=styled.div`
margin:30px 0 40px;
`
export const AvatarPic=styled.div`
width:48px;
display:inline-block;
vertical-align:middle;
.avatar{
    height:100%;
    width:100%
    border-radius:24px;
    border:1px solid #ddd;
}
`

export const WriterInfo=styled.div`
display:inline-block;
margin-left:8px;
vertical-align:middle;
line-height:1.42857;
.name{
    margin-right:3px;
    font-size:16px;
    text-decoration:none;   
    color:#333;
}
.wujiaoPic{
    width:20px;
        height:20px;
        border-radis:0;
        margin-right:5px;
    background:url(${wujiaoPic})
}
.meta{
    margin-top:5px;
    font-size:12px;
    color:#969696;
    .item{

        padding-right:5px;
    }
}


`
export const QrcodeWrapper=styled.div`
position:fixed;
top:80px;
right:150px;
`
export const PaperList=styled.div`


`
