    render(){
      return (
          <InfoWrapper>
              <div>{this.props.title}</div>
              <Writer>

              </Writer>
          </InfoWrapper>
      )
    }
}

 const mapStateToProps=(state)=>({
    title:state.getIn(['detail','title'])
})

export default connect(mapStateToProps,null)(Info);
