import styled from 'styled-components';

export const HomeWrapper=styled.div`

width:960px;
margin:0 auto;
background:green;
`
export const HomeLeft=styled.div`
float:left
width:625px;
padding-left:15px;
padding-top:30px;
.banner-img{
    width:625px;
    height:270px;
    margin-bottom:30px;
}
`
export const HomeRight=styled.div`

float:right;
margin-left:40px;
padding-top:30px;
`
export const ListItem=styled.div`
overflow:hidden;

float:left;
padding:15px 2px 20px 0;
margin-bottom:15px;
border-bottom:1px solid #f0f0f0;

.pic{
    vertical-align:middle;
    display:block;
    float:right;
    width:150px;
    height:100px;


    border-radius:4px;
}
`
export const ListLine=styled.div`
margin:-5px 0 15px;
border-top:1px solid #f0f0f0;
`

export const ListInfo=styled.div`
float:left;
width:458px;
margin-right:15px;
font-size:13px;
line-height:24px;
color:#999;
box-sizing:border-box;
.title{
    margin:-7px 0 4px;
    font-size:18px;
    font-weight:700;
    line-height:1.5;
    color:#333;
    display: inherit;
}
.desc{
    line-height:24px;
    font-size:13px;
    margin:0 0 8px;
}
`

export const ListAciton=styled.div`
padding-right: 0!important;
font-size:12px;
font-weight:400;
line-height:20px;
.action {
    margin-right:10px;
    color:#b4b4b4;
    text-decoration:none;
}
.iconfont{
    font-size:13px;
    margin-right:2px;


}
`
